import { CourseActionType } from '../actions/course.action';
// //create a dummy initial state
const initialState = [{
  EmployeesId: '',
  department: 'Computer Engineering',
  name: 'C++ Programming'
}];
export function CourseReducer(state = initialState, action) {
  switch (action.type) {
    case CourseActionType.ADD_ITEM:
      return [...state, action.payload];
    default:
      return state;
  }
}
